id awt01_fetch(void) async;
id awt01_mainOnly(id seed) __attribute__((objc_executor(main)));

actor class AWT01Counter : NSObject
- (void)bump;
- (int)current;
@end

int awt01_parse_operands(AWT01Counter *counter, id seed) async __attribute__((objc_executor(global))) {
    id token = await awt01_fetch();
    id mainToken = await awt01_mainOnly(seed);
    await [counter bump];
    return await [counter current];
}
