typedef int cap_handle_t;
void cap01_consume_id(id value);
void cap01_consume_handle(cap_handle_t value);

void cap01_parse_modifiers(id strongOwner, id weakOwner, id unsafePeer, cap_handle_t fd) {
    void (^handler)(void) = [strong strongOwner, weak weakOwner, unowned unsafePeer, move fd] {
        cap01_consume_id(strongOwner);
        cap01_consume_id(weakOwner);
        cap01_consume_id(unsafePeer);
        cap01_consume_handle(fd);
    };
    handler();
}
