#pragma objc_language_version(3)
int m06a002_fetch(void) async;

typedef struct M06A002_Result {
    int value;
    int span;
} M06A002_Result;

M06A002_Result m06a002_execute(int gate) async {
    M06A002_Result out = { 0, 0 };
    if (gate) {
        out.value = try await m06a002_fetch();
        out.span = 22;
    }
    return out;
}
