#pragma objc_language_version(3)
int m06a009_fetch(void) async;

typedef struct M06A009_Trace {
    int value;
    int span_start;
    int span_end;
} M06A009_Trace;

M06A009_Trace m06a009_invariant(int gate) async {
    M06A009_Trace trace = { 0, 10, 20 };
    if (gate) {
        trace.value = try await m06a009_fetch();
    }
    return trace;
}
