actor class ACT01Counter : NSObject
- (int)current;
- (void)bump;
@end

int act01_cross_actor_missing_await(ACT01Counter *counter) async {
    [counter bump];
    return [counter current];
}

int act01_cross_actor_with_await(ACT01Counter *counter) async {
    await [counter bump];
    return await [counter current];
}
