id act03_yield_once(void) async;

actor class ACT03TurnBox : NSObject
@property (atomic) int turn;
- (int)observeTurnChange:(int)seed async;
@end

@implementation ACT03TurnBox
- (int)observeTurnChange:(int)seed async {
    int before = self.turn;
    await act03_yield_once();
    int after = self.turn;
    return before == after ? seed : (seed + 1);
}
@end
