id awt03_asyncCall(void) async;
id awt03_mainOnly(id seed) __attribute__((objc_executor(main)));

actor class AWT03Counter : NSObject
- (void)bump;
@end

id awt03_missing_all(AWT03Counter *counter, id seed) async __attribute__((objc_executor(global))) {
    id first = awt03_asyncCall();
    id second = awt03_mainOnly(seed);
    [counter bump];
    return first ? first : second;
}
