id awt04_asyncCall(void) async;
id awt04_mainOnly(id seed) __attribute__((objc_executor(main)));
id awt04_syncValue(void);

actor class AWT04Counter : NSObject
- (int)current;
@end

id awt04_valid_and_unnecessary(AWT04Counter *counter, id seed) async __attribute__((objc_executor(global))) {
    id first = await awt04_asyncCall();
    id second = await awt04_mainOnly(seed);
    int current = await [counter current];
    id stable = await awt04_syncValue();
    return current > 0 ? stable : (first ? first : second);
}
