typedef struct BRWCell {
    const char *slot;
} BRWCell;

BRWCell *brw_make_cell(void);

@interface BRWOwner : NSObject {
@public
    const char *ivarSlot;
}
@property (nonatomic, assign) const char *propSlot;
@end

void brw_neg02_store_heap_ivar_property(BRWOwner *owner, borrowed const char *bytes) {
    BRWCell *cell = brw_make_cell();
    cell->slot = bytes;
    owner->ivarSlot = bytes;
    owner.propSlot = bytes;
}
