typedef id CAN01TaskHandle;
CAN01TaskHandle can01_spawn_child(id (^work)(void) async) __attribute__((objc_task_spawn));
id can01_join_throwing(CAN01TaskHandle handle) async throws;
void can01_request_parent_cancellation(void);
int can01_task_is_cancelled(void);

id can01_parent_to_child(id seed) async throws {
    CAN01TaskHandle child = can01_spawn_child(^id(void) async {
        if (can01_task_is_cancelled() != 0) {
            return nil;
        }
        return seed;
    });
    can01_request_parent_cancellation();
    return try await can01_join_throwing(child);
}
