typedef id CAN02TaskHandle;
CAN02TaskHandle can02_spawn_child(id (^work)(void) async) __attribute__((objc_task_spawn));
CAN02TaskHandle can02_spawn_detached(id (^work)(void) async) __attribute__((objc_task_detached));
id can02_join_nonthrowing(CAN02TaskHandle handle) async;
void can02_request_parent_cancellation(void);
int can02_task_is_cancelled(void);

id can02_detached_does_not_inherit(id seed) async {
    CAN02TaskHandle child = can02_spawn_child(^id(void) async {
        return can02_task_is_cancelled() != 0 ? nil : seed;
    });
    CAN02TaskHandle detached = can02_spawn_detached(^id(void) async {
        return can02_task_is_cancelled() != 0 ? nil : seed;
    });
    can02_request_parent_cancellation();
    (void)child;
    return await can02_join_nonthrowing(detached);
}
