typedef id CAN03TaskGroup;
id can03_with_group(id (^body)(CAN03TaskGroup group) async throws) async throws __attribute__((objc_task_group));
void can03_group_add(CAN03TaskGroup group, id (^work)(void) async throws);
id can03_group_wait_next(CAN03TaskGroup group) async throws;
id can03_make_group_failure(void);
int can03_task_is_cancelled(void);
id can03_checkpoint(void) async;

id can03_group_failure_cancels_siblings(id seed) async throws {
    return try await can03_with_group(^id(CAN03TaskGroup group) async throws {
        can03_group_add(group, ^id(void) async throws {
            throw can03_make_group_failure();
        });
        can03_group_add(group, ^id(void) async throws {
            while (can03_task_is_cancelled() == 0) {
                (void)await can03_checkpoint();
            }
            return seed;
        });
        return try await can03_group_wait_next(group);
    });
}
