typedef int can04_handle_t;
void can04_close(can04_handle_t handle);
void can04_cancellation_checkpoint(void) async throws;

int can04_cleanup_on_cancellation_unwind(void) async throws {
    int trace = 0;
    can04_handle_t handle __attribute__((objc_resource(close=can04_close, invalid=-1))) = 42;
    defer { trace = trace * 10 + 1; };
    try await can04_cancellation_checkpoint();
    (void)handle;
    return trace;
}
