typedef int cap_handle_t;
void cap05_close(cap_handle_t handle);
cap_handle_t cap05_open(void);
int cap05_is_valid(cap_handle_t handle);

int cap05_use_after_move_capture(void) {
    cap_handle_t fd __attribute__((objc_resource(close=cap05_close, invalid=-1))) = cap05_open();
    void (^handler)(void) = [move fd] {
        (void)fd;
    };
    if (cap05_is_valid(fd)) {
        handler();
        return 1;
    }
    return 0;
}
