id exe01_mainOnly(id seed) __attribute__((objc_executor(main)));

id exe01_mainOnly(id seed) {
    return seed;
}

id exe01_hopWithAwait(id seed) async __attribute__((objc_executor(global))) {
    id hopped = await exe01_mainOnly(seed);
    return hopped;
}

id exe01_missingAwait(id seed) async __attribute__((objc_executor(global))) {
    return exe01_mainOnly(seed);
}
