id exe02_mainLeaf(id seed) __attribute__((objc_executor(main)));

id exe02_mainLeaf(id seed) {
    return seed;
}

id exe02_mainChain(id seed) __attribute__((objc_executor(main)));
id exe02_mainChain(id seed) {
    id first = exe02_mainLeaf(seed);
    id second = exe02_mainLeaf(first);
    return second;
}

id exe02_mainAsyncChain(id seed) async __attribute__((objc_executor(main))) {
    return exe02_mainLeaf(seed);
}
