@protocol Persistable
@end

@interface Record : NSObject <Persistable>
@end

@interface Vault<__covariant T : id<Persistable>> : NSObject
- (T)peek;
@end

id consumeErased(Vault<id<Persistable>> *value);

id acceptCovariantErasure(Vault<Record *> *records) {
    Vault<id<Persistable>> *erasedView = records;
    return consumeErased(erasedView);
}
