id _Nonnull consume(id _Nonnull value);

id _Nonnull refinedAfterGuard(id _Nullable maybeValue) {
    guard let value = maybeValue else {
        return @"fallback";
    }

    match (value) {
      case let bound: {
        return consume(bound);
      }
      default: {
        return @"fallback";
      }
    }
}
