id m01b004_async_call(void) async;
id m01b004_main_only(id seed) __attribute__((objc_executor(main)));

id m01b004_positive(id seed) async __attribute__((objc_executor(main))) {
    id value = await m01b004_async_call();
    return m01b004_main_only(seed);
}

id m01b004_negative(id seed) async __attribute__((objc_executor(global))) {
    id value = m01b004_main_only(seed);
    return value;
}
