actor class M01B005Counter : NSObject
@property (atomic) int count;
- (int)positiveSnapshot;
- (int)negativeSnapshot __attribute__((objc_nonisolated));
@end

@implementation M01B005Counter
- (int)positiveSnapshot {
    return self.count;
}

- (int)negativeSnapshot {
    return self.count;
}
@end
