typedef id M01B006TaskHandle;
M01B006TaskHandle m01b006_spawn(id (^work)(void) async) __attribute__((objc_task_spawn));
id m01b006_join(M01B006TaskHandle handle) async;

actor class M01B006ActorBox : NSObject
@property (atomic) int value;
@end

@interface M01B006MutableBox : NSObject
@property (nonatomic) int value;
@end

id m01b006_positive(M01B006ActorBox *box) async {
    M01B006TaskHandle handle = m01b006_spawn(^id(void) async {
        return box;
    });
    return await m01b006_join(handle);
}

id m01b006_negative(M01B006MutableBox *box) async {
    M01B006TaskHandle handle = m01b006_spawn(^id(void) async {
        return box;
    });
    return await m01b006_join(handle);
}
