typedef int m01b008_handle_t;
void m01b008_close(m01b008_handle_t handle);
m01b008_handle_t m01b008_open(void);
int m01b008_is_valid(m01b008_handle_t handle);

int m01b008_positive(void) {
    m01b008_handle_t fd __attribute__((objc_resource(close=m01b008_close, invalid=-1))) = m01b008_open();
    m01b008_handle_t moved = take(fd);
    fd = m01b008_open();
    return (int)fd + (int)moved;
}

int m01b008_negative(void) {
    m01b008_handle_t fd __attribute__((objc_resource(close=m01b008_close, invalid=-1))) = m01b008_open();
    m01b008_handle_t moved = take(fd);
    if (m01b008_is_valid(fd)) {
        return (int)fd;
    }
    return (int)moved;
}
