id m04b010_async_call(void) async;
id m04b010_main_only(id seed) __attribute__((objc_executor(main)));

id m04b010_positive(id seed) async __attribute__((objc_executor(main))) {
    id value = await m04b010_async_call();
    return m04b010_main_only(seed);
}

id m04b010_negative(id seed) async __attribute__((objc_executor(global))) {
    id value = m04b010_main_only(seed);
    return value;
}
