// RUN: objc3c -fobjc-version=3 -fobjc3-feature-opt-meta=off -fsyntax-only %s
#if __OBJC3_FEATURE_DERIVE_EQUALITY__ || __OBJC3_FEATURE_DERIVE_HASH__ || __OBJC3_FEATURE_DERIVE_DEBUGDESCRIPTION__ || __OBJC3_FEATURE_DERIVE_CODABLE__
#error "META-DRV-01 expects derive feature macros to be disabled when OPT-META is off"
#endif

#if __OBJC3_FEATURE_DERIVE_EQUALITY__
__attribute__((objc_derive("Equality")))
@interface METADRV01Model : NSObject
@property (nonatomic) int value;
@end
#else
@interface METADRV01Model : NSObject
@property (nonatomic) int value;
- (BOOL)isEqual:(id _Nullable)other;
@end
#endif
