// RUN: objc3c -fobjc-version=3 -fobjc3-feature-match=off -fsyntax-only %s
int classifyWithFallback(int value) {
#if __OBJC3_FEATURE_MATCH__
    match (value) {
      case 0: {
        return 10;
      }
      case let observed: {
        return observed;
      }
    }
#else
    if (value == 0) {
        return 10;
    }
    return value;
#endif
}

int rejectUnguardedMatchWhenDisabled(int value) {
    match (value) {
      case 0: {
        return 1;
      }
      default: {
        return 0;
      }
    }
}
