typedef int res_handle_t;
void res_close(res_handle_t handle);
res_handle_t res_open(void);
int res_is_valid(res_handle_t handle);

int res03_use_after_take(void) {
    res_handle_t fd __attribute__((objc_resource(close=res_close, invalid=-1))) = res_open();
    res_handle_t moved = take(fd);
    if (res_is_valid(fd)) {
        return (int)fd;
    }
    return (int)moved;
}
