typedef id SND01TaskHandle;
typedef struct SND01Pair {
    int left;
    unsigned long right;
} SND01Pair;

SND01TaskHandle snd01_spawn(id (^work)(void) async) __attribute__((objc_task_spawn));
id snd01_join(SND01TaskHandle handle) async;
id snd01_pack_pair(SND01Pair value);

id snd01_scalar_aggregate_capture(int seed, unsigned long salt) async {
    SND01Pair pair = { seed, salt };
    SND01TaskHandle handle = snd01_spawn(^id(void) async {
        return snd01_pack_pair(pair);
    });
    return await snd01_join(handle);
}
