typedef id SND03TaskHandle;
SND03TaskHandle snd03_spawn(id (^work)(void) async) __attribute__((objc_task_spawn));
SND03TaskHandle snd03_detached(id (^work)(void) async) __attribute__((objc_task_detached));
id snd03_join(SND03TaskHandle handle) async;

@interface SND03MutableBox : NSObject
@property (nonatomic) int value;
@end

id snd03_nonsendable_capture(SND03MutableBox *box) async {
    SND03TaskHandle child = snd03_spawn(^id(void) async {
        return box;
    });
    SND03TaskHandle detached = snd03_detached(^id(void) async {
        return box;
    });
    (void)child;
    return await snd03_join(detached);
}
