@interface SND04MutableBox : NSObject
@property (nonatomic) int value;
@end

actor class SND04Channel : NSObject
- (void)put:(SND04MutableBox * _Nonnull)value;
- (SND04MutableBox * _Nonnull)take;
@end

SND04MutableBox *snd04_cross_actor_boundary(SND04Channel *channel, SND04MutableBox *box) async {
    await [channel put:box];
    return await [channel take];
}
