typedef id SND06TaskHandle;
SND06TaskHandle snd06_spawn(id (^work)(void) async) __attribute__((objc_task_spawn));
id snd06_join(SND06TaskHandle handle) async;

@interface SND06MutableBox : NSObject
@property (nonatomic) int value;
@end

id snd06_weak_unowned_capture(SND06MutableBox *box) async {
    id (^weakTask)(void) async = [weak box] {
        return box;
    };
    id (^unownedTask)(void) async = [unowned box] {
        return box;
    };
    SND06TaskHandle first = snd06_spawn(weakTask);
    SND06TaskHandle second = snd06_spawn(unownedTask);
    (void)first;
    return await snd06_join(second);
}
