id loadPrimary(id seed) throws;
id loadSecondary(id seed) throws;
id<Error> upgradeFailure(void);

id? resolveDisplay(id seed) {
    do {
        do {
            id value = try loadPrimary(seed);
            return value;
        } catch {
            if (seed == nil) {
                throw upgradeFailure();
            }
            id? fallback = try? loadSecondary(seed);
            return fallback?;
        }
    } catch {
        return nil;
    }
}
