id loadPrimary(id seed) throws;
id loadSecondary(id seed) throws;
id<Error> upgradeFailure(void);

id badPropagation(id seed) throws {
    do {
        do {
            id? candidate = try? loadPrimary(seed);
            return candidate?;
        } catch {
            throw upgradeFailure();
        }
    } catch {
        return loadSecondary(seed);
    }
}
